INSERT INTO `jobs` VALUES (1,'成本主管','Cost Accounting Supervisor',9,0,'<ul>\r\n	<li>\r\n		Key member in supporting the upcoming corporate ERP implementation in the general/ cost accounting modules and systems by developing effective and accurate costing accounting process and procedures</li>\r\n	<li>\r\n		支持公司即将实施的常规采购，成本会计模块和系统，发展有效和精确的成本计算工艺和程序</li>\r\n	<li>\r\n		Reconcile and analyze inventory, variances, mfg labor &amp; overhead, and closely supporting production and material supply operation</li>\r\n	<li>\r\n		调和及分析库存、变动、生产部人工和日常开支，支持产品和材料供应操作</li>\r\n	<li>\r\n		Analyze and make proper judgment in accruing installation and warranty, E&amp;O, and scrap liabilities</li>\r\n	<li>\r\n		分析并对安装和保修、错误排查以及报废品作出适当的判断</li>\r\n	<li>\r\n		Prepare all COGS related journal entries and be part of monthly and quarterly closing and analysis</li>\r\n	<li>\r\n		整理与所有分类账目有关的销售成本，作为每月和每季的结算与分析</li>\r\n	<li>\r\n		Accurately track project costing and make cost reduction proposals</li>\r\n	<li>\r\n		精确追踪项目花销，作出成本降低的提案</li>\r\n</ul>\r\n','<ul>\r\n	<li>\r\n		Bachelor in accounting</li>\r\n	<li>\r\n		本科会计学位</li>\r\n	<li>\r\n		5 year plus manufacturing company costing experiences a must, preferably in hi-tech equipment company</li>\r\n	<li>\r\n		必须具有五年以上制造业公司成本管理经验，在高科技设备公司尤佳</li>\r\n	<li>\r\n		Experienced in inventory management, inventory count, fixed asset</li>\r\n	<li>\r\n		在库存管理，库存统计，固定资产方面有经验</li>\r\n	<li>\r\n		Excellent Excel and other database skills</li>\r\n	<li>\r\n		出色的Excel和其他数据库技能</li>\r\n	<li>\r\n		SAP, Oracle or other ERP implementation experiences highly desirable</li>\r\n	<li>\r\n		SAP，Oracle和其他ERP实施经验者优先</li>\r\n	<li>\r\n		Hard working and capable of multi-task</li>\r\n	<li>\r\n		吃苦耐劳，能够同时处理多件事</li>\r\n	<li>\r\n		Great team player with pleasant personality</li>\r\n	<li>\r\n		团队精神</li>\r\n	<li>\r\n		Self-starter, strong attention to details and well organized</li>\r\n	<li>\r\n		工作主动，对细节充分把控，有组织有纪律</li>\r\n	<li>\r\n		Strong verbal and written communication, analytical and decision-making skills</li>\r\n	<li>\r\n		出色的口语和书面沟通能力，分析和决策能力</li>\r\n	<li>\r\n		Enjoy working for fast growing hi-tech venture</li>\r\n	<li>\r\n		乐于从事高速发展的高科技先驱工作</li>\r\n</ul>\r\n','2011-09-20 20:47:07','2011-09-20 20:47:07');
